USE master
IF EXISTS (SELECT *
FROM sysdatabases
WHERE name='Lightbookdb')
DROP DATABASE Lightbookdb
CREATE DATABASE Lightbookdb
GO
USE Lightbookdb

CREATE TABLE [Authors]
(
	[ID] INT IDENTITY(1,1) PRIMARY KEY,
	[Name]NVARCHAR(100),
)
GO

CREATE TABLE [Catalogs]
(
	[ID] INT IDENTITY(1,1) PRIMARY KEY,
	[Name]NVARCHAR(100),
)
GO

CREATE TABLE [Categories]
(
	[ID] INT IDENTITY(1,1) PRIMARY KEY,
	[Name]NVARCHAR(100),
)
GO

CREATE TABLE [Books]
(
	[ID] INT IDENTITY(1,1) PRIMARY KEY,
	[Name]NVARCHAR(100) NOT NULL,
	[Price] INT NOT NULL,
	[Review]NVARCHAR(max),
	[Picture]VARCHAR(500),
	[Chapter]nvarchar(max),
	[CategoryID]INT NOT NULL,
	[RatePoint] FLOAT,
	FOREIGN KEY(CategoryID) REFERENCES [Categories](ID),
)
GO

CREATE TABLE [AuthorBooks]
(
	[ID] INT IDENTITY(1,1) PRIMARY KEY,
	[AuthorID]INT NOT NULL,
	[BookID]INT NOT NULL,
	FOREIGN KEY(AuthorID) REFERENCES [Authors](ID),
	FOREIGN KEY(BookID) REFERENCES [Books](ID),
)
GO

CREATE TABLE [CatalogBooks]
(
	[ID] INT IDENTITY (1,1) PRIMARY KEY,
	[CatalogID]INT NOT NULL,
	[BookID]INT NOT NULL,
	FOREIGN KEY (CatalogID) REFERENCES [Catalogs](ID),
	FOREIGN KEY(BookID) REFERENCES [Books](ID),
)
GO

Create Table [Roles]
(
	[ID] Int IDENTITY(1,1) PRIMARY Key,
	[Name] Nvarchar(100) not null,
)
Go

CREATE TABLE [Users]
(
	[ID] INT IDENTITY(1,1) PRIMARY KEY,
	[Name]NVARCHAR(100) NOT NULL,
	[Avatar]VARCHAR(1000) NOT NULL,
	[Username]VARCHAR(100) NOT NULL,
	[Password]VARCHAR(100) NOT NULL,
	[PhoneNumber]VARCHAR(10),
	[RoleID] int not null,
	FOREIGN KEY (RoleID) REFERENCES [Roles](ID),
)
GO

CREATE TABLE [Favorites]
(
	[ID] INT IDENTITY(1,1) PRIMARY KEY,
	[UserID]INT NOT NULL,
	[BookID]INT NOT NULL,
	FOREIGN KEY (UserID) REFERENCES [Users](ID),
	FOREIGN KEY(BookID) REFERENCES [Books](ID),
)
GO

CREATE TABLE [Comments]
(
	[ID] INT IDENTITY(1,1) PRIMARY KEY,
	[Content] NTEXT NOT NULL,
	[UserID]INT NOT NULL,
	[BookID]INT NOT NULL,
	[RatePoint] FLOAT,
	[DateComment] DATETIME DEFAULT (getdate()) NOT NULL,
	FOREIGN KEY (UserID) REFERENCES [Users](ID),
	FOREIGN KEY(BookID) REFERENCES [Books](ID),
)
GO

CREATE TABLE [BookUsers]
(
	[ID] INT IDENTITY(1,1) PRIMARY KEY,
	[Content] NTEXT NOT NULL,
	[UserID]INT NOT NULL,
	[BookID]INT NOT NULL,
	[LastRead] DATETIME,
	[Chapter] INT DEFAULT 0,
	[IsFinished] BIT DEFAULT 0,
	FOREIGN KEY (UserID) REFERENCES [Users](ID),
	FOREIGN KEY(BookID) REFERENCES [Books](ID),
)
GO

CREATE TABLE [Carts]
(
	[ID] INT IDENTITY(1,1) PRIMARY KEY,
	[UserID]INT NOT NULL,
	[TotalPrice]INT NOT NULL,
	FOREIGN KEY (UserID) REFERENCES [Users](ID),
)
GO

CREATE TABLE [CartDetails]
(
	[ID] INT IDENTITY(1,1) PRIMARY KEY,
	[CartID]INT NOT NULL,
	[BookID]INT NOT NULL,
	[TotalPrice]INT NOT NULL,
	FOREIGN KEY(CartID) REFERENCES [Carts](ID),
	FOREIGN KEY(BookID) REFERENCES [Books](ID),
)
GO

